import TeamList from './components/TeamList.js'
import Filter from './components/Filter.js'

class App extends Component {

  state = {
    allStudents: [],
    teamStudents: []
  }

  componentDidMount = () => {
    fetch(getGryffindor)
      .then(resp => resp.json())
      .then(students => students.members.map(student => {
        fetch(student_.id)
      }))
  }

  render(){
    console.log(this.state);
    return (
      <div id="character-container">

      </div>
    )
  }
}

export default App;
